package javaPractice;

public class SwitchConditionalStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
switch(a)
{
case 1:
	System.out.println("executing case1 method");
	break;
case 2:
	System.out.println("executing case2 method");
	break;
case 3:
	System.out.println("executing case3 method");
	break;
	default:
		System.out.println("executing the default method");
}
}
	}


